mapper.update:
UPDATE tb_friend


        is_defriend = #{isDefriend},


        status=#{status},


        special_attention=#{special_attention}


WHERE current_account_id =#{currentAccountId} and friend_id=#{friendId}

mapper.select:
select

from tb_friend f left JOIN tb_group_friend g on f.current_account_id = g.current_account_id
WHERE f.current_account_id =#{currentAccountId}  and g.group_id = #{groupId}
