mapper.select:
SELECT id,

from tb_function f left join tb_group_function gf on f.id= gf.function_id left tb_group_friend tgf on tgf.group_id = gf.group_id
WHERE gf.current_account_id = #{friendId} and tgf.friend_id = #{curentAccountId}

SELECT id,

from tb_function f left join tb_conference_function cf on f.id = cf.function_id
WHERE cf.conference_id = #{conferenceId} and cf.sponsor_id = #{sponsorId} and cf.member_id = {memberId}
